class Hello{

	def log;
	def context;
	def testRunner;

	def Hello(log, context, testRunner){
		this.log = log;
		this.context = context;
		this.testRunner = testRunner;
	}
	def sayHello(String name){
		log.info ("Saying Hello Now To "+ name);
	}
}

//to instantiate a class object

context.setProperty("Hello", new Hello(log, context, testRunner));

project = testRunner.testCase.testSuite.project ;
 tcase = project.testSuites["TestSuite 3"].testCases["TestCase 1"] ; 
 hello = tcase.getTestStepByName("Hello");

hello.run(testRunner, context)

context.Hello.sayHello("Amit")

//Groovy - run request from same TestCase

def status = testRunner.runTestStepByName("GetCountryISOCode")
log.info(status)
def result = status.getStatus().toString();
log.info ("   ---- "+result)

if(result == "OK"){
	log.info("Test Passed")
}
else{
	log.info("Test Failed")
	}

// Running GetCountryISOCode from other test case
// Test Case 2 

//Groovy - run request from another TestCase or TestSuite

project = testRunner.testCase.testSuite.project ;
tcase = project.testSuites["TestSuite 3"].testCases["TestCase 2"] ; 
tstep = tcase.getTestStepByName("GetCountryISOCode");
def status = tstep.run(testRunner, context)
def result = status.getStatus().toString();
log.info ("   ----   "+result)


//failed as assertion condition failed

